package main;

/*
 * (Area and perimeter of a rectangle) Write a program that displays the area 
 * and perimeter of a rectangle with the width of 4.5 and height of 7.9 using the following formula:
				
								area = width * height
*/


public class Solution01_09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Area of Rectangular : " + (4.5 * 7.9));
		System.out.println("Perimeter of Rectangular : " + (2 * (4.5 + 7.9)));
	}

}